// Verificar si es un webhook de verificaci√≥n
if ($input.item.json.hub && $input.item.json.hub.mode === 'subscribe') {
  return [{
    json: {
      type: 'verification',
      challenge: $input.item.json.hub.challenge,
      verify_token: $input.item.json.hub.verify_token
    }
  }];
}

// Procesar mensaje de WhatsApp
const body = $input.item.json.body;
if (!body || !body.entry || !body.entry[0] || !body.entry[0].changes || !body.entry[0].changes[0]) {
  return [];
}

const change = body.entry[0].changes[0];
if (!change.value || !change.value.messages || !change.value.messages[0]) {
  return [];
}

const message = change.value.messages[0];
const from = message.from;
const messageId = message.id;
const timestamp = new Date().toISOString();

// Extraer texto del mensaje
let text = '';
if (message.text && message.text.body) {
  text = message.text.body.toLowerCase().trim();
} else if (message.button && message.button.text) {
  text = message.button.text.toLowerCase().trim();
} else if (message.interactive) {
  if (message.interactive.button_reply) {
    text = message.interactive.button_reply.id;
  } else if (message.interactive.list_reply) {
    text = message.interactive.list_reply.id;
  }
}

return [{
  json: {
    from: from,
    text: text,
    messageId: messageId,
    timestamp: timestamp,
    originalMessage: message
  }
}];

// Motor 1: Nuevo Paciente
const session = {
  motor_type: 1,
  step: 'collect_name',
  patient_data: {},
  created_at: new Date().toISOString()
};

